function lazyLoadCats(){let n=new IntersectionObserver(onChangeC,{root:null,rootMargin:"0px",threshold:.3}),t=document.querySelectorAll(".category-img");t.forEach(t=>n.observe(t))}function onChangeC(n){n.forEach(n=>{if(n.intersectionRatio>0&&n.target.getAttribute("src")=="../Content/placeholder.jpg"){var t=new Image;t.src=n.target.getAttribute("data-img");t.onload=function(){$(n.target).fadeOut(250,function(){n.target.setAttribute("src",t.src);$(n.target).fadeIn(250)})}}})}$(document).ready(function(){$(document).on("click",".categories",function(){$("#categories-preview").slideToggle()});$(document).on("click","body, html",function(n){$(n.target).parents("#categories-preview").length||n.target.id=="categories-preview"||n.target.id=="category"||(n.stopPropagation(),$("#categories-preview").hide())})});